let flag = false;
filterSelection("all");

function filterSelection(c) {
    
  var x, i;
  x = document.getElementsByClassName("filterProjects");
  if (c == "all") c = "";
  let kk = document.querySelector("#myBtnContainer .active");
  kk.className = kk.className.replace("active", "");
  if(!flag){
      flag = true;
      document.querySelector("#myBtnContainer button").className += " active";
  }
  
  for (i = 0; i < x.length; i++) {
    removeClass(x[i], "show-project");
    if (x[i].className.indexOf(c) != -1){
        addClass(x[i], "show-project");
    }
  }
}

// Show filtered elements
function addClass(element, name) {
  var i, arr1, arr2;
  arr1 = element.className.split(" ");
  arr2 = name.split(" ");
  for (i = 0; i < arr2.length; i++) {
    if (arr1.indexOf(arr2[i]) == -1) {
      element.className += " " + arr2[i];
    }
  }
}

// Hide elements that are not selected
function removeClass(element, name) {
  var i, arr1, arr2;
  arr1 = element.className.split(" ");
  arr2 = name.split(" ");
  for (i = 0; i < arr2.length; i++) {
    while (arr1.indexOf(arr2[i]) > -1) {
      arr1.splice(arr1.indexOf(arr2[i]), 1);
    }
  }
  element.className = arr1.join(" ");
}

